mapper.sql:
WHERE	1	=	1

	AND	ID	=	#{id}


	AND NAME like '%${name}%'


	AND	STATUS	=	#{status}


	AND	STATUS	<> '2'

mapper.update:
    	UPDATE ROLES SET
			NAME		=	#{name},
			STATUS 		=	#{status},
			MODIFIEDBY	=	#{modifiedBy},
			MODIFIEDDATE	=	#{modifiedDate}
    	WHERE ID	=	#{id}

    	UPDATE ROLES SET
    		STATUS	=	'2'  
    	WHERE	1	=	1

			AND	ID	=	#{id}


			ADN	NAME	=	#{name}
