mapper.select:
SELECT DISTINCT APP.* FROM APPS APP,GROUP_PRIVILEGES GP WHERE APP.ID=GP.APPID AND GP.GROUPID IN( SELECT G.ID FROM GROUPS G WHERE G.ID='ALL_USER_GROUP' OR G.ID IN( SELECT GM.GROUPID FROM GROUP_MEMBER GM,USERINFO U WHERE 1 = 1
AND U.ID = #{userId}
AND U.USERNAME = #{username}
AND GM.MEMBERID = U.ID ) )
AND NAME = #{name}
ORDER BY SORTINDEX
