mapper.sql:
WHERE	1	=	1

	AND	ID	=	#{id}


	AND NAME like '#${name}#'


	AND	STATUS	=	#{status}


	AND	STATUS	<> '2'

mapper.select:
SELECT U.ID As ID, U.USERNAME as USERNAME, U.PASSWORD AS PASSWORD, U.USERTYPE AS USERTYPE, U.CREATEDDATE AS CREATEDDATE, U.MODIFIEDDATE AS MODIFIEDDATE, U.STATUS AS STATUS, U.CREATEDBY AS CREATEDBY, U.MODIFIEDBY AS MODIFIEDBY FROM USERINFO U WHERE U.STATUS = '1' AND U.ID IN ( SELECT RU.UID FROM ROLE_USER RU WHERE RU.ROLEID = #{roleId} )
AND U.USERNAME = #{username}
${orderBy}

    	SELECT
    		 COUNT(ID)
    	FROM 
    		USERINFO
    	WHERE  	STATUS = '1'

				AND  USERNAME	= 	#{username}


				AND  EMAIL = #{email} 

    		AND	ID IN ( 
    				SELECT UID FROM ROLE_USER WHERE ROLEID  = #{roleId} 
    		)

SELECT U.ID As ID, U.USERNAME as USERNAME, U.PASSWORD AS PASSWORD, U.USERTYPE AS USERTYPE, U.CREATEDDATE AS CREATEDDATE, U.MODIFIEDDATE AS MODIFIEDDATE, U.STATUS AS STATUS, U.CREATEDBY AS CREATEDBY, U.MODIFIEDBY AS MODIFIEDBY FROM USERINFO U WHERE U.STATUS = '1'
AND U.USERNAME = #{username}
AND U.EMAIL = #{email}
AND U.ID NOT IN ( SELECT RU.UID FROM ROLE_USER RU WHERE RU.ROLEID = #{roleId} )
${orderBy}

    	SELECT
    		 COUNT(ID)
    	FROM 
    		USERINFO
    	WHERE  	STATUS = '1'

				AND  USERNAME	= 	#{username}



				AND  EMAIL = #{email} 

    		AND	ID NOT IN ( 
    				SELECT UID FROM ROLE_USER WHERE ROLEID  = #{roleId}
    		)
