mapper.select:
SELECT DISTINCT IG.* FROM `MXK_GROUPS` IG, `MXK_GROUPS` G, MXK_GROUP_MEMBER GM WHERE 1 = 1 AND GM.GROUPID = G.ID AND GM.MEMBERID = IG.ID AND GM.TYPE = 'GROUP'
AND GM.GROUPID = #{groupId} AND G.ID = #{groupId}
AND G.NAME = #{groupName}

mapper.update:
INSERT INTO MXK_GROUP_MEMBER( ID, GROUPID, MEMBERID, TYPE ) SELECT CONCAT_WS('-','UD','${id}',U.ID) ID, '${id}' GROUPID, U.ID MEMBERID, 'USER-DYNAMIC' TYPE FROM MXK_USERINFO U WHERE NOT EXISTS( SELECT 1 FROM MXK_GROUP_MEMBER GM WHERE GM.GROUPID=#{id} AND GM.MEMBERID=U.ID AND GM.TYPE='USER-DYNAMIC' )
${filters}
AND U.DEPARTMENTID IN( ${orgIdsList})

mapper.delete:
DELETE FROM MXK_GROUP_MEMBER GM WHERE TYPE = 'USER-DYNAMIC' AND GM.GROUPID=#{id} AND NOT EXISTS( SELECT 1 FROM MXK_USERINFO U WHERE 1 = 1 AND U.ID=GM.MEMBERID
${filters}
AND U.DEPARTMENTID IN( ${orgIdsList})
)
